trigger.script:
if IsQuestTaken("d_FatherRoomInMinin_Quest") and not(IsQuestComplete("d_FatherRoomInMinin_Quest")) then TActivate( "RolikBegin_FATHERHOUSE" )
CompleteQuest( "d_FatherRoomInMinin_Quest" )
AddHistory( "r1_Father_Room_00" ) AddFadingMsgId( "fm_history_got" ) AddQuestItem("quest_key_for_submarine") end
if IsQuestTaken("r3m2_RideToFatherRoomAfterOracul") and not(IsQuestComplete("r3m2_RideToFatherRoomAfterOracul")) then TActivate( "RolikBegin_FATHERHOUSERETURN" )
CompleteQuest( "r3m2_RideToFatherRoomAfterOracul" ) end

if IsQuestTaken("r2m1_DeployBloks") and not(IsQuestComplete("r2m1_DeployBloks") or IsQuestFailed("r2m1_DeployBloks")) then TakeQuest( "r1m3_DestroyOracle" )
TActivate( "RolikBegin_ORACLEAGRESSOR" )
trigger:Deactivate() end
